    "id": 20,
    //Sound Recording Id (you can have a Sound Recording associated with multiple Revenue Stream Sound Recordings)
    "soundRecordingId": 48,
    //When the RSSR started attributing to revenues
    "startDate": "2022-01-02T00:00:00.000Z",
    //When the RSSR stopped attributing to revenues
    "endDate": "2023-01-02T00:00:00.000Z",
    //The Id of the revenue stream the RSSR is associated to
    "streamId": 5,
    //The overall status of the RSSR possible values are:
    //Licensed - the RSSR is fully licensed
