    //"No Matches Found" - No matches were found for this RSSR
    //Unlicensed - none of the matches found have matched with any license agreements
    //Processing - The RSSR is still processing and so a license status is not known at this time
    "status": "Licensed"
    //The sound recording data which is attached to the revenue stream
    "soundRecording": {
        //The Id of the sound recording (SR)
        "id": 48,
        //The calculated duration of the SR (seconds)
        "duration": 60.00326530612245,
